import React, { useState, useEffect, useRef } from 'react';
import ResultsShowcase from '../features/ResultsShowcase';
import ReviewInsights from '../features/ReviewInsights';

const initialConversation = [
  { from: 'system', text: 'Welcome to Constellate! ✦ Let\'s find your ideal home among the stars. What city or area are you interested in?' }
];

export default function ConversationWindow() {
  const [conversation, setConversation] = useState(initialConversation);
  const [input, setInput] = useState('');
  const [results, setResults] = useState([]);
  const [selectedHome, setSelectedHome] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // New filter states
  const [location, setLocation] = useState('');
  const [budget, setBudget] = useState('');
  const [utility, setUtility] = useState('');

  const nlwebRef = useRef(null);

  useEffect(() => {
    if (window.NLWeb && nlwebRef.current) {
      window.NLWeb.init({
        container: nlwebRef.current,
        // ...other config options as needed...
      });
    }
  }, []);

  // NLWeb MCP Integration
  const queryNLWeb = async (query) => {
    try {
      setIsLoading(true);

      // Initialize MCP connection
      const initResponse = await fetch('http://localhost:8000/mcp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          jsonrpc: "2.0",
          id: 1,
          method: "initialize",
          params: {
            protocolVersion: "2024-11-05",
            capabilities: {},
            clientInfo: { name: "constellate-web", version: "1.0" }
          }
        })
      });

      // Send initialized notification
      await fetch('http://localhost:8000/mcp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          jsonrpc: "2.0",
          method: "notifications/initialized",
          params: {}
        })
      });

      // Query NLWeb
      const queryResponse = await fetch('http://localhost:8000/mcp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          jsonrpc: "2.0",
          id: 2,
          method: "tools/call",
          params: {
            name: "ask",
            arguments: {
              query: query,
              site: ["zillow", "apartments", "reddit", "googlemaps_reviews"]
            }
          }
        })
      });

      const result = await queryResponse.json();

      if (result.result && result.result.content && result.result.content[0]) {
        return result.result.content[0].text;
      } else {
        return "I'm having trouble connecting to the constellation network. Please try again.";
      }
    } catch (error) {
      console.error('NLWeb query error:', error);
      return "The stars seem to be misaligned. Let me help you with a basic search instead.";
    } finally {
      setIsLoading(false);
    }
  };

  // Enhanced home search with NLWeb integration
  const fetchHomes = async (searchQuery) => {
    try {
      // Try NLWeb first
      const nlwebResponse = await queryNLWeb(searchQuery);

      // Add NLWeb response to conversation
      setConversation(prev => [...prev, { 
        from: 'system', 
        text: `✦ Constellation Response: ${nlwebResponse}` 
      }]);

      // Fallback to mock data if needed
      setResults([
        { id: 1, address: '123 Stellar Lane, Springfield', price: '$350,000', rating: '4.8' },
        { id: 2, address: '456 Nebula Ave, Metropolis', price: '$420,000', rating: '4.6' },
        { id: 3, address: '789 Galaxy Blvd, Springfield', price: '$380,000', rating: '4.9' }
      ]);
    } catch (error) {
      console.error('Search error:', error);
      setResults([
        { id: 1, address: '123 Main St, Springfield', price: '$350,000' },
        { id: 2, address: '456 Oak Ave, Metropolis', price: '$420,000' }
      ]);
    }
  };

  const handleSend = async () => {
    if (!input.trim() && !location && !budget && !utility) return;

    const userMessage = input || `Looking for homes in ${location} with budget ${budget} and needs: ${utility}`;
    setConversation([...conversation, { from: 'user', text: userMessage }]);

    await fetchHomes(userMessage);
    setInput('');
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20, border: '1px solid #ccc', borderRadius: 8, background: 'rgba(10,17,36,0.95)' }}>
      <h2>Conversation Window</h2>
      <div style={{ marginBottom: 16 }}>
        <label>
          Location:&nbsp;
          <input value={location} onChange={e => setLocation(e.target.value)} placeholder="City or ZIP" />
        </label>
        <label style={{ marginLeft: 16 }}>
          Budget:&nbsp;
          <input value={budget} onChange={e => setBudget(e.target.value)} placeholder="e.g. $300,000" />
        </label>
        <label style={{ marginLeft: 16 }}>
          Utility Needs:&nbsp;
          <input value={utility} onChange={e => setUtility(e.target.value)} placeholder="e.g. EV charger" />
        </label>
      </div>
      <ul>
        {conversation.map((msg, idx) => (
          <li key={idx}><b>{msg.from === 'system' ? 'Assistant' : 'You'}:</b> {msg.text}</li>
        ))}
      </ul>
      <div style={{ margin: '16px 0', color: '#7ecbff' }}>
        <b>Tips for your search:</b>
        <ul>
          <li>Consider commute times and local amenities.</li>
          <li>Check school ratings and neighborhood safety.</li>
          <li>Think about future resale value.</li>
        </ul>
      </div>
      <input
        value={input}
        onChange={e => setInput(e.target.value)}
        placeholder="Type your preferences..."
        style={{ width: '80%' }}
      />
      <button onClick={handleSend}>Send</button>
      <ResultsShowcase results={results} onSelect={setSelectedHome} />
      {selectedHome && <ReviewInsights home={selectedHome} />}
      <div ref={nlwebRef} />
    </div>
  );
}
